INSERT INTO `hss_recycle_bin` VALUES ('80', '1', 'Áo Khoác Jeans Tay Len Bạn Có Dám Thử', '_News', 'a:1:{i:0;O:8:\"stdClass\":20:{s:2:\"id\";s:3:\"482\";s:3:\"uid\";s:1:\"2\";s:5:\"catid\";s:3:\"437\";s:8:\"cat_name\";N;s:9:\"cat_alias\";s:4:\"blog\";s:5:\"title\";s:45:\"Áo Khoác Jeans Tay Len Bạn Có Dám Thử\";s:11:\"title_alias\";s:37:\"ao-khoac-jeans-tay-len-ban-co-dam-thu\";s:5:\"intro\";s:345:\"Là một tín đồ thời trang thông minh, không gì là tốn kém nếu bạn đầu tư cho mình một chiếc áo khoác jeans vô cùng tiện dụng. Với cách “biến tấu” đơn giản sau bạn sẽ có cho mình một chiếc áo khoác jeans tay len vô cùng độc đáo và vô cùng tiết kiệm trong mùa hè này!\";s:7:\"content\";s:4861:\"<p>\r\n	Bạn muốn một chiếc &aacute;o kho&aacute;c jeans cực &ldquo;chất&rdquo; v&agrave; lạ? Bạn kh&ocirc;ng muốn mất qu&aacute; nhiều tiền nhưng vẫn c&oacute; một chiếc ao kho&aacute;c jeans thời trang xuống phố? Bạn ch&aacute;n với kiểu &aacute;o kho&aacute;c jeans cũ kỹ của m&igrave;nh? Chỉ với một ch&uacute;t kh&eacute;o l&eacute;o bạn c&oacute; thể biến h&oacute;a chiếc &aacute;o kho&aacute;c jeans cũ th&agrave;nh chiếc &aacute;o kho&aacute;c jeans tay len khỏe khoắn, trẻ trung v&agrave; độc đ&aacute;o.</p>\r\n<p style=\"text-align: center;\">\r\n	<br />\r\n	<img alt=\"\" src=\"/uploads/images/blog/ao-khoac-jeans.jpg\" style=\"width: 500px; height: 499px;\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<div>\r\n	Để s&aacute;ng tạo chiếc &aacute;o kho&aacute;c jeans tay len độc đ&aacute;o n&agrave;y, bạn cần chuẩn bị một số vật liệu đơn giản sau nh&eacute;:</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	&ndash; &Aacute;o kho&aacute;c hay &aacute;o vest jeans cắt tay (Khi cắt tay của &aacute;o kho&aacute;c bạn cần để lại một khoảng 3cm so với đường viền vai &aacute;o)</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	&ndash; Tay &aacute;o len loại d&agrave;y.</div>\r\n<div>\r\n	&ndash; K&eacute;o may.</div>\r\n<div>\r\n	&ndash; Chỉ.</div>\r\n<div>\r\n	&ndash; M&aacute;y kh&acirc;u.</div>\r\n<div>\r\n	&ndash; Ghim c&agrave;i.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%202.jpg\" style=\"width: 500px; height: 545px;\" /></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%203.jpg\" style=\"width: 500px; height: 449px;\" /></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<div>\r\n		Ch&uacute;ng ta c&ugrave;ng bắt tay v&agrave;o l&agrave;m chiếc &aacute;o kho&aacute;c jeans tay len độc đ&aacute;o nh&eacute;!</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong>Bước 1: Lộn tr&aacute;i &aacute;o denim v&agrave; ghim tay &aacute;o len v&agrave;o phần th&acirc;n.</strong></div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"/uploads/images/blog/jean%204.jpg\" style=\"width: 500px; height: 402px;\" /></div>\r\n	<div style=\"text-align: center;\">\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		<img alt=\"\" src=\"/uploads/images/blog/jeans%205.jpg\" style=\"width: 500px; height: 501px;\" /></div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%206.jpg\" style=\"width: 500px; height: 402px;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>Bước 2: Trước khi kh&acirc;u, lộn phải &aacute;o v&agrave; kiểm tra xem phần tay &aacute;o c&oacute; vừa với phần vai hay kh&ocirc;ng.&nbsp;</strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<strong><img alt=\"\" src=\"/uploads/images/blog/jeans%207.jpg\" style=\"width: 500px; height: 402px;\" /></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>Bước 3 : &nbsp;D&ugrave;ng m&aacute;y kh&acirc;u để nối hai phần lại với nhau. Bạn n&ecirc;n may s&aacute;t với phần đường chỉ kh&acirc;u của &aacute;o denim để phần nối kh&ocirc;ng bị lộ r&otilde;. &nbsp;</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%208.jpg\" style=\"width: 600px; height: 483px;\" /></p>\r\n<p>\r\n	Sau khi may xong, chiếc &aacute;o sẽ tr&ocirc;ng như thế n&agrave;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%209.jpg\" style=\"width: 500px; height: 402px;\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>Bước 4 : Bạn h&atilde;y l&agrave;m tương tự với tay &aacute;o c&ograve;n lại nh&eacute;!</strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%2010.jpg\" style=\"width: 500px; height: 416px;\" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Vậy l&agrave; sản phẩm đ&atilde; ho&agrave;n th&agrave;nh. Chắc chắn bạn sẽ thấy chiếc &aacute;o &ldquo;mới&rdquo; của m&igrave;nh kh&aacute;c lạ v&agrave; đặc biệt hơn nhiều so với ban đầu. Bạn c&oacute; thể mix c&ugrave;ng &aacute;o thun in h&igrave;nh c&aacute; t&iacute;nh, quần skinny v&agrave; mang bốt cổ thấp trẻ trung l&agrave; c&oacute; thể g&acirc;y ấn tượng với người đối diện về một c&ocirc; n&agrave;ng năng động, phong c&aacute;ch.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/uploads/images/blog/jeans%2011.jpg\" style=\"width: 600px; height: 483px;\" /></p>\";s:3:\"img\";s:46:\"07-2015/01-07-37-16-07-2015-ao-khoac-jeans.jpg\";s:7:\"created\";s:10:\"1437582320\";s:3:\"hot\";s:1:\"1\";s:5:\"focus\";s:1:\"1\";s:4:\"week\";s:1:\"1\";s:8:\"order_no\";s:1:\"1\";s:8:\"view_num\";s:1:\"0\";s:10:\"meta_title\";s:45:\"Áo Khoác Jeans Tay Len Bạn Có Dám Thử\";s:13:\"meta_keywords\";s:45:\"Áo Khoác Jeans Tay Len Bạn Có Dám Thử\";s:16:\"meta_description\";s:160:\"Là một tín đồ thời trang thông minh, không gì là tốn kém nếu bạn đầu tư cho mình một chiếc áo khoác jeans vô cùng tiện dụng\";s:6:\"status\";s:1:\"1\";}}', '07-2015/01-07-37-16-07-2015-ao-khoac-jeans.jpg', 'news', '1447227476');
